  console.log("DB2");


//  mongoose.Promise = global.Promise;mongoose.connect("mongodb://root:retrobot2018@ds239071.mlab.com:39071/retrobotdb");

  fs.readdir("../commands", (err, files) => {
    console.log("DB3");
    if (err)
      console.log(err);
    else
      console.log("Files: " + files);
    console.log("DB4");
